mapper.select:
select count(*) from notification

  has_delete = false

    and target_user_id = #{targetUserId,jdbcType=BIGINT}


    and userId = #{userId,jdbcType=BIGINT}


    and notice_type IN

      #{noticeType}

select * from notification

  has_delete = false

    and target_user_id = #{targetUserId,jdbcType=BIGINT}


    and user_id = #{userId,jdbcType=BIGINT}


    and notice_type IN

      #{noticeType}



order by create_time desc
LIMIT #{pageParam.size} OFFSET #{pageParam.offset}

select * from notification

  has_delete = false

    and user_id = #{userId,jdbcType=BIGINT}


    and target_user_id = #{targetUserId,jdbcType=BIGINT}


    and article_id = #{articleId,jdbcType=BIGINT}


    and comment_id = #{commentId,jdbcType=BIGINT}


    and notice_type = #{noticeType,jdbcType=INTEGER}

  order by create_time desc

mapper.update:
update notification set already_read = 1

  target_user_id = #{targetUserId,jdbcType=BIGINT}

    and notice_type IN

      #{noticeType}


  and already_read = 0

update notification
set has_delete = true



      and user_id = #{userId,jdbcType=BIGINT}


      and target_user_id = #{targetUserId,jdbcType=BIGINT}


      and source_comment_id = #{sourceCommentId,jdbcType=BIGINT}


      and article_id = #{articleId,jdbcType=BIGINT}


      and article_comment_id = #{articleCommentId,jdbcType=BIGINT}


      and notice_type = #{noticeType,jdbcType=INTEGER}

update notification
set has_delete = true
where id = #{id,jdbcType=BIGINT}

mapper.select:
select count(*) from notification
has_delete=false and already_read = 0
and target_user_id = #{targetUserId,jdbcType=BIGINT}
and notice_type IN
#{noticeType}

select * from notification

  has_delete = false

    and target_user_id = #{targetUserId,jdbcType=BIGINT}


    and notice_type IN

      #{noticeType}


  order by create_time desc
  limit 1;
