mapper.update:
update thumb_up
set has_delete = true



      and user_id = #{userId,jdbcType=BIGINT}


      and article_id = #{articleId,jdbcType=BIGINT}


      and comment_id = #{commentId,jdbcType=BIGINT}


      and thumb_up_type = #{thumbUpType,jdbcType=INTEGER}

mapper.select:
select count(*) from thumb_up

  has_delete = false

    and user_id = #{userId,jdbcType=BIGINT}


    and article_id = #{articleId,jdbcType=BIGINT}


    and comment_id = #{commentId,jdbcType=BIGINT}


    and thumb_up_type = #{thumbUpType,jdbcType=INTEGER}

select * from thumb_up

  has_delete = false

    and user_id = #{userId,jdbcType=BIGINT}


    and article_id = #{articleId,jdbcType=BIGINT}


    and comment_id = #{commentId,jdbcType=BIGINT}



    and thumb_up_type = #{thumbUpType,jdbcType=INTEGER}


order by create_time desc
LIMIT #{pageParam.size} OFFSET #{pageParam.offset}

select * from thumb_up

  has_delete = false

    and user_id = #{userId,jdbcType=BIGINT}


    and article_id = #{articleId,jdbcType=BIGINT}


    and comment_id = #{commentId,jdbcType=BIGINT}


